const { network, ethers, deployments } = require("hardhat");
const {assert, expect } = require("chai");
const { developmentChains } = require("../../helper-hardhat-config");
const chai = require('chai');
const eventemitter2 = require('chai-eventemitter2');

chai.use(eventemitter2());

const lowSvgUri = "data:image/svg+xml;base64,"
const highSvgUri = "data:image/svg+xml;base64,"

const lowSvgTokenUri = "data:application/json;base64,"
const highSvgTokenUri = "data:application/json;base64,"

!developmentChains.includes(network.name) ? describe.skip : describe("Dynamic SVG Nft", () => {

    let deployer, dynamicSvgNft, mockV3Aggregator
    
    beforeEach(async () => {
        const accounts = await ethers.getSigners();
        deployer = accounts[0];
        await deployments.fixture(["mocks", "dynamicSvg"]);
        dynamicSvgNft = await ethers.getContract("DynamicSvgNft")
        mockV3Aggregator = await ethers.getContract("MockV3Aggregator")
    })

    describe("Constructor", () => {
        it("Sets Constructor Value Correctly", async() => {
            const lowSvg = await dynamicSvgNft.getLowSvg()
            const highSvg = await dynamicSvgNft.getHighSvg()
            const priceFeed = await dynamicSvgNft.getPriceFeed()
            const tokenCounter = await dynamicSvgNft.getTokenCounter()
            assert.equal(lowSvg, lowSvgUri)
            assert.equal(highSvg, highSvgUri)
            assert.equal(mockV3Aggregator.address, priceFeed)
            assert.equal(tokenCounter, 0)
        })
    })

    describe("mintNft", () => {
        it("Mints NFT", async() => {
            const highValue = ethers.utils.parseEther("1");
            await dynamicSvgNft.mintNft(highValue);
            const tokenId = await dynamicSvgNft.getTokenCounter();
            const nftOwner = await dynamicSvgNft.ownerOf(tokenId.toString());
            assert.equal(nftOwner, deployer.address)
        })
        it("Increases Counter", async() => {
            const beforeTokenId = await dynamicSvgNft.getTokenCounter();
            const highValue = ethers.utils.parseEther("1");
            await dynamicSvgNft.mintNft(highValue);
            const AftertokenId = await dynamicSvgNft.getTokenCounter();
            assert(beforeTokenId.add(1).toString(), AftertokenId.toString())
        })

        it("Sets High value", async() => {
            const highValue = ethers.utils.parseEther("1");
            await dynamicSvgNft.mintNft(highValue);
            const tokenId = await dynamicSvgNft.getTokenCounter();

            const highValueNft = dynamicSvgNft.s_tokenIdToHighValue(tokenId.toString())

            assert(highValue.toString(), highValueNft.toString())
        })

        it("Emits an Event", async() => {
            const highValue = ethers.utils.parseEther("1");
            await expect(dynamicSvgNft.mintNft(highValue)).to.emit(dynamicSvgNft, "CreatedNft");
        })
    })

    describe("Token Uri", () => {
        it("Provides happy NFT if price is greater than high value", async() => {
            const highValue = ethers.utils.parseEther("1");
            await dynamicSvgNft.mintNft(highValue);
            const tokenId = await dynamicSvgNft.getTokenCounter()

            const tokenUri = await dynamicSvgNft.tokenURI(tokenId.toString())

            assert.equal(tokenUri.toString(), highSvgTokenUri)
        })

        it("Provides frown NFT if price is less than high value", async() => {
            const highValue = ethers.utils.parseEther("1000000000");
            await dynamicSvgNft.mintNft(highValue);
            const tokenId = await dynamicSvgNft.getTokenCounter()

            const tokenUri = await dynamicSvgNft.tokenURI(tokenId.toString())

            assert.equal(tokenUri.toString(), lowSvgTokenUri)
        })
    })
})